mapper.update:
UPDATE PATIENT
SET PA_ADM_DT = #{paAdmDt}
WHERE PA_NO = #{paNo}

UPDATE ADMISSION_CHART
	SET ADRM_ROOMNO = #{adrmRoomno},
		ADRM_BEDNO = #{adrmBedno},
		EMP_NO = #{empNo},
		ADMI_FILE = #{admiFile}
WHERE ADMI_NO = #{admiNo}

UPDATE ADMISSION_ROOM
	SET PA_NO = #{paNo}
WHERE ADRM_ROOMNO = #{adrmRoomno} AND ADRM_BEDNO = #{adrmBedno}

UPDATE PATIENT
	SET PA_ADM_DT = 'AMYN01',
		PA_NOKNM = #{paNoknm},
		PA_NOKPH = #{paNokph}
WHERE PA_NO = #{paNo}

UPDATE ADMISSION_CHART
	SET ADMI_FILE = #{admiFile}
WHERE ADMI_NO = #{admiNo}

UPDATE ADMISSION_CHART
	SET ADMI_OUTDT = SYSDATE
WHERE ADMI_NO = #{admiNo}

UPDATE ADMISSION_ROOM
	SET PA_NO = NULL
WHERE ADRM_ROOMNO = #{adrmRoomno} AND ADRM_BEDNO = #{adrmBedno}

UPDATE PATIENT
	SET PA_ADM_DT = 'AMYN02'
WHERE PA_NO = #{paNo}

UPDATE CLINIC_CHART
	SET PA_ADM_DT = #{admiNo}
WHERE CLINIC_NO = #{clinicNo}

mapper.select:
SELECT ADMI_NO
FROM ADMISSION_CHART
WHERE PA_NO = #{paNo}

SELECT ADRM_ROOMNO, ADMI_OUTDT, ADMI_INDT
FROM ADMISSION_CHART
WHERE ADMI_NO = #{admiNo}

SELECT *
FROM ADMISSION_CHART
WHERE EXTRACT(YEAR FROM ADMI_INDT) = #{year}
AND EXTRACT(MONTH FROM ADMI_INDT) = #{month}
