mapper.select:
SELECT DS_NO, EMP_NO, DS_STR_DATE, DS_END_DATE, DS_CATE, DS_CONT, DS_AD
FROM DOC_SCHEDULE
WHERE SUBSTR(DS_STR_DATE, 1, 4) = #{year}
AND SUBSTR(DS_END_DATE, 1, 4) = #{year}

SELECT DS_NO, EMP_NO, DS_STR_DATE, DS_END_DATE, DS_CATE, DS_CONT, DS_AD
FROM DOC_SCHEDULE
WHERE SUBSTR(DS_STR_DATE, 1, 4) = #{year}
AND SUBSTR(DS_END_DATE, 1, 4) = #{year}
AND EMP_NO = #{empNo}

SELECT DS_NO, DS_STR_DATE, DS_END_DATE, DS_CATE, DS_CONT, DS_AD
FROM DOC_SCHEDULE
WHERE SUBSTR(DS_STR_DATE, 1, 4) = #{year}
AND SUBSTR(DS_END_DATE, 1, 4) = #{year}
AND EMP_NO = #{empNo}

SELECT DS_NO, EMP_NO, DS_STR_DATE, DS_END_DATE, DS_CATE, DS_CONT, DS_AD
FROM DOC_SCHEDULE
WHERE DS_NO = #{dsNo}

mapper.update:
UPDATE DOC_SCHEDULE
SET DS_STR_DATE=#{dsStrDate}, DS_END_DATE=#{dsEndDate}, DS_CATE=#{dsCate}, DS_CONT=#{dsCont}, DS_AD=#{dsAd}
WHERE DS_NO = #{dsNo}
