mapper.insert:
SELECT NOTICE_BOARD_SEQ.nextval FROM DUAL
INSERT INTO NOTICE_BOARD (NOTICE_NO, EMP_NO, NOTICE_TITLE, NOTICE_CONT, NOTICE_DT, NOTICE_VIEWS
,ATCH_FILE_CD
) VALUES (#{noticeNo}, #{empNo}, #{noticeTitle}, #{noticeCont}, SYSDATE, 0
,#{atchFileCd}
)

mapper.update:
UPDATE NOTICE_BOARD
	set
	NOTICE_TITLE=#{noticeTitle}, 
	NOTICE_CONT=#{noticeCont}
WHERE NOTICE_NO=#{noticeNo}

UPDATE notice_board
SET    ATCH_FILE_CD = #{atchFileCd, jdbcType=VARCHAR}
WHERE  NOTICE_NO = #{noticeNo}
