mapper.update:
		UPDATE CLINIC_CHART
		SET CLINIC_INJNM = #{clinicInjnm},
    		CLINIC_OPINION = #{clinicOpinion},
    		CLINIC_STATE = #{clinicState}
		WHERE HSRP_NO = #{hsrpNo}

mapper.select:
SELECT CLINIC_NO
FROM CLINIC_CHART
WHERE HSRP_NO = #{hsrpNo}

mapper.update:
UPDATE CLINIC_CHART
SET CLINIC_STATE = #{clinicState}
WHERE CLINIC_NO = #{clinicNo}
